import './main.css'
import himalaya from 'data:image/jpeg;base64,';
import fort from 'data:image/jpeg;base64,';   
import arvallis from 'data:image/jpeg;base64,';
import { createRoot } from 'react-dom/client'

const navbarStyle = {
  backgroundColor: "#2c3e50",
  padding: "10px",
  marginBottom: "20px",
  textAlign: "center",
  padding:"20px"
};

function Navbar(){
  return(
    <div className="navbar" style={navbarStyle}>
      <h1 style={{color:"white", textAlign:"center"}}>Mountain Ranges of India</h1>
    </div>
  )
}

function Card(props){
  const {image, heading , subheading} = props;
  return(
    <div className="card">
      <img src={image} alt="image" className="card-image"/>
      <div className="container">
        <h4 style={{textAlign:"center", color:"black"}}><b>{heading}</b></h4>
        <p>{subheading}</p>
      </div>
    </div>
  )
}

const footerStyle = {
  backgroundColor:"#2c3e50",
  color:"white",
  textAlign:"center",
  padding:"10px",
  marginTop:"20px"
};

function Footer(){
  return (
    <div className="footer" style={footerStyle}>
      <p>Â© 2024 Mountain Ranges of India. All rights reserved.</p>
    </div>
  )
}

createRoot(document.getElementById('root')).render(
  <>
    <Navbar/>
    <div className="card-container">
      <Card  
        image={himalaya} 
        heading={"Himalaya"} 
        subheading={
          "The Himalayas, a majestic mountain range..."
        } 
      />

      <Card  
        image={fort} 
        heading={"Forts"} 
        subheading={
          "India is home to many magnificent forts such as Rajgad, Sinhagad, Golconda, Red Fort, and Amber Fort. These forts showcase the architectural brilliance, bravery, and cultural heritage of ancient kingdoms."
        } 
      />

      <Card  
        image={arvallis} 
        heading={"Aravalli Range"} 
        subheading={
          "The Aravalli Range is one of the oldest mountain ranges in India..."
        } 
      />
    </div>

    <Footer/>
  </>
);
